mapper.select:
select decode(count(FOLLOWS),0,'N','Y')
from follow 
where m_No=#{mNo}
and FOLLOWS=#{follows}

update.foreach:
UPDATE 
       STORAGE_BOX 
   SET SB_NAME=#{sb.sbName}
 WHERE
       M_NO = #{sb.mNo}
   AND SB_NO = #{sb.sbNo}

mapper.delete:
		DELETE 
		FROM STORAGE_BOX
		WHERE M_NO=#{mNo} 
		AND SB_NO in
		
		#{sbNos}
